        const marketplaceRate = 0.01; // Example rate, adjust as needed
        const marketplaceAddress = "1HrtBtoHw1EDfVb8ayZVSijXr6sC4YT1Mi"; // Replace with actual address

        purchaseOrdinal(outpoint, marketplaceRate, marketplaceAddress);
    };

    return (
        <div className={`rounded-lg overflow-hidden m-4 p-4 bg-blue-200 border-4 border-black shadow-xl `}>
