import { useState, useEffect } from "react";
import { PandaConnectButton } from "../components/PandaConnectButton";
import {
  Addresses,
  SignedMessage,
  usePandaWallet,
} from "panda-wallet-provider";

const initProvider = () => {
  if ("panda" in window) {
    const provider = window.panda;

    if (provider?.isReady) {
      return provider;
    }
  }

  window.open(
    "https://chromewebstore.google.com/detail/panda-wallet/mlbnicldlpdimbjdcncnklfempedeipj",
    "_blank"
  );
};

export const SamplePage = () => {
  const wallet = usePandaWallet();
  const [pubKey, setPubKey] = useState<string | undefined>();
  const [addresses, setAddresses] = useState<Addresses | undefined>();
  const [ordinals, setOrdinals] = useState<any[]>([]);

  useEffect(() => {
    const fetchData = async () => {
      if (wallet && wallet.isConnected) {
        const isConnected = await wallet.isConnected();
        if (isConnected) {
          const addrs = await wallet.getAddresses();
          if (addrs) setAddresses(addrs);
          console.log(addrs);
          const ords = await wallet.getOrdinals();
          if (ords) setOrdinals(ords);
          console.log(ords);
        }
      }
    };

    if (wallet) {
      fetchData();
    }
  }, [wallet]);

  const handleConnect = async () => {
    if (!wallet.connect) {
      window.open(
        "https://github.com/Panda-Wallet/panda-wallet#getting-started-alpha",
        "_blank"
      );
      return;
    }
    const key = await wallet.connect();
    if (key) setPubKey(key);
  };

  const renderOrdinalCards = () => {
    return ordinals.map((ordinal, index) => (
      <div key={index} className="p-4 m-2 border rounded shadow-lg">
        {/* Customize this part as needed */}
        <p className="text-sm text-black ">{JSON.stringify(ordinal)}</p>
      </div>
    ));
  };

  return (
    <div className="App">
      <div className="bg-gray">
        <header className="text-center p-6">
          {/* <img
          src={pandaIcon}
          alt="Panda Wallet Icon"
          className="mx-auto h-20 w-20"
        /> */}
          <h1 className="text-2xl font-bold">Panda Wallet Demo</h1>
          <PandaConnectButton
            className="mt-4 p-2 bg-blue-500 text-white rounded hover:bg-blue-600"
            onClick={handleConnect}
          />
          {pubKey && (
            <p className="mt-2 text-sm text-gray-700">Public Key: {pubKey}</p>
          )}
          {addresses && (
            <p className="mt-2 text-sm text-gray-700">
              Addresses: {JSON.stringify(addresses)}
            </p>
          )}
          {ordinals.length > 0 && (
            <div className="mt-4">
              <h4 className="text-lg font-semibold text-black">
                Your Ordinals:
              </h4>
              <div className="flex flex-wrap justify-center">
                {renderOrdinalCards()}
              </div>
            </div>
          )}
        </header>
      </div>
    </div>
  );
};
