import { useState, useEffect } from 'react';

const OGCards = ({ address, ordinal }) => {
  // States for storing fetched data and verification status
  const [ordinalsData, setOrdinalsData] = useState([]);
  const [verificationStatus, setVerificationStatus] = useState({});

  // Function to fetch ordinals data
  useEffect(() => {
    const fetchOrdinalsData = async () => {
      try {
        const response = await fetch(`https://v3.ordinals.gorillapool.io/api/txos/address/${address}/unspent`);
        const data = await response.json();
        const filteredData = data.filter(item => item.data?.insc?.words?.includes('og'));
        setOrdinalsData(filteredData);
      } catch (error) {
        console.error('Error fetching ordinals data:', error);
      }
    };

    if (address) {
      fetchOrdinalsData();
    }
  }, [address]);

  // Function to verify a record
  const verifyRecord = async (item) => {
    // Logic from the script to verify a record
    // Update verificationStatus state based on the result
  };

  return (
    <div className="ordinal-cards-container">
      {ordinalsData.map((item, index) => (
        <div key={index} className="ordinal-card">
          <div>{item.data.insc.text}</div>
          <button onClick={() => verifyRecord(item)}>Verify</button>
          <div className="verification-status">
            {verificationStatus[item.txid] || 'Pending'}
          </div>
        </div>
      ))}
    </div>
  );
};

export default OGCards;
