    if (!Buffer.isBuffer(buf)) {
      throw new Error('Input must be a buffer')
    }
    const checkedBuf = Buffer.alloc(buf.length + 4)
    const hash = Hash.sha256Sha256(buf)
    buf.copy(checkedBuf)
    hash.copy(checkedBuf, buf.length)
