    this.buf = buf
    return this
  }

  fromString (str) {
    const buf = Base58Check.decode(str)
    this.buf = buf
    return this
  }

  toBuffer () {
    return this.buf
  }

  toString () {
    return Base58Check.encode(this.buf)
  }
