  static sortPubKeys (pubKeys) {
    return pubKeys.slice().sort((pubKey1, pubKey2) => {
      const buf1 = pubKey1.toBuffer()
      const buf2 = pubKey2.toBuffer()
      const len = Math.max(buf1.length, buf2.length)
      for (let i = 0; i <= len; i++) {
        if (buf1[i] === undefined) {
          return -1 // shorter strings come first
        }
        if (buf2[i] === undefined) {
          return 1
        }
        if (buf1[i] < buf2[i]) {
          return -1
        }
        if (buf1[i] > buf2[i]) {
          return 1
        } else {
          continue
        }
      }
    })
