  isScriptHashOut () {
    const buf = this.toBuffer()
    return (
      buf.length === 23 &&
        buf[0] === OpCode.OP_HASH160 &&
        buf[1] === 0x14 &&
        buf[22] === OpCode.OP_EQUAL
