  static async asyncCalcrecovery (sig, pubKey, hashBuf) {
    const workersResult = await Workers.asyncClassMethod(
      Ecdsa,
      'calcrecovery',
      [sig, pubKey, hashBuf]
    )
    return new Sig().fromFastBuffer(workersResult.resbuf)
