      time: this.time,
      bits: this.bits,
      nonce: this.nonce
    }
  }

  fromBr (br) {
    this.versionBytesNum = br.readUInt32LE()
    this.prevBlockHashBuf = br.read(32)
    this.merkleRootBuf = br.read(32)
    this.time = br.readUInt32LE()
    this.bits = br.readUInt32LE()
    this.nonce = br.readUInt32LE()
