      txOutNum: json.txOutNum,
      scriptVi:
          typeof json.scriptVi !== 'undefined'
            ? VarInt.fromJSON(json.scriptVi)
            : undefined,
      script:
          typeof json.script !== 'undefined'
            ? Script.fromJSON(json.script)
            : undefined,
      nSequence: json.nSequence
