import rarityData from '../rarity.json';

//Categorize items
const clothingItems = ["Trousers", "Bronze Boots", "Ninja Mask", "Dark Knight Greaves", /* more clothing items... */];
const weaponItems = ["Derringer", "Bow", "Estoc", "Falchion", "Naginata", /* more weapon items... */];

// Define base stats
const baseStats = {
  "Trousers": { type: "clothing", defense: 5 },
  "Bronze Boots": { type: "clothing", defense: 7 },
  // ... more items with their base stats
  "Derringer": { type: "weapon", attack: 10 },
  "Bow": { type: "weapon", attack: 12 },
  // ... more weapons with their base stats
};

// Define modifiers for prefixes and suffixes
const modifiers = {
  prefixes: {
    "Gold": 1.1, // 10% increase
    "Adamantium": 1.2, // 20% increase
    // ... more prefixes
  },
  suffixes: {
    "of Spirit": 1.05, // 5% increase
    "of Dexterity": 1.05, // 5% increase
    // ... more suffixes
  }
};


// Function to calculate final stats
const calculateStats = (itemName) => {
  let stats = baseStats[itemName] ? {...baseStats[itemName]} : null;
  if (!stats) return null;

  rarityData.prefixes.forEach(([prefix, _]) => {
    if (itemName.includes(prefix) && modifiers.prefixes[prefix]) {
      stats.defense = stats.defense ? stats.defense * modifiers.prefixes[prefix] : stats.defense;
      stats.attack = stats.attack ? stats.attack * modifiers.prefixes[prefix] : stats.attack;
    }
  });

  rarityData.suffixes.forEach(([suffix, _]) => {
    if (itemName.includes(suffix) && modifiers.suffixes[suffix]) {
      stats.defense = stats.defense ? stats.defense * modifiers.suffixes[suffix] : stats.defense;
      stats.attack = stats.attack ? stats.attack * modifiers.suffixes[suffix] : stats.attack;
    }
  });

  return stats;
};


const OrdinalCard = ({ ordinal }) => {

  const getRarity = (name) => {
    const parts = name.split(' ');
    const hasPrefix = parts.length > 1 && parts[0] !== 'of';
    const hasSuffix = parts.includes('of');

    if (hasPrefix && hasSuffix) return 'Rare';
    if (hasPrefix || hasSuffix) return 'Uncommon';
    return 'Common';
  };

  const stats = calculateStats(ordinal.data.insc.text);
  const rarity = getRarity(ordinal.data.insc.text);

  const rarityStyles = {
    Common: 'border-black text-black shadow-common',
    Uncommon: 'border-blue-500 text-blue-500 shadow-uncommon',
    Rare: 'border-yellow-500 text-yellow-500 shadow-rare',
  };

  return (
    <div className={`rounded overflow-hidden m-4 p-4 bg-white border-2 ${rarityStyles[rarity]} hover:bg-gray-200  hover:extrabold`}>
      <div className="px-6 py-4">
        <div className="font-bold text-lg mb-2">{ordinal.data.insc.text}</div>
        <p className="text-gray-700 text-base">{rarity}</p>
        {/* <p className="text-gray-600 text-sm">
          Description: {ordinal.description || "N/A"}
        </p> */}
      </div>
    </div>
  );
};

export default OrdinalCard;
