    ).sort((a, b) => {
      // Assuming the price is stored in `listing.data.list.price` and is a number
      const numA = extractNumber(a?.data?.list?.price?.toString());
      const numB = extractNumber(b?.data?.list?.price?.toString());
      return (numA ?? 0) - (numB ?? 0);
    });

    return (
